mapper.insert:
set @year_id = 0;
set @time_start = now();
set @time_end = now();
select @year_id := year_id,@time_start := year_start_time,@time_end := year_end_time
from number_of_sessions where the_year=#{theYear};
insert into t_winner_team(team_id, team_name, team_user_list, gmt_create,
gmt_modified, the_year, team_begin_time, team_end_time, competition_id,
team_order_num, team_order_text)
values(#{teamId},#{teamName},#{teamUserList},#{gmtCreate},#{gmtModified},@year_id,@time_start,
@time_end,#{competitionId},#{teamOrderNum},#{teamOrderText})

mapper.update:
set @year_id = 0;
select @year_id := year_id from number_of_sessions where the_year=#{theYear};
update t_winner_team


        team_name=#{teamName},


        team_user_list=#{teamUserList}


where the_year=@year_id
and team_id=#{teamId} and competition_id=#{competitionId}

mapper.delete:
set @year_id = 0;
select @year_id := year_id from number_of_sessions where the_year=#{theYear};
delete from t_winner_team where team_id=#{teamId} and
the_year=@year_id and competition_id=#{competitionId} limit 1
