mapper.select:
select style_show.*, competition.competition_name from t_style_show style_show
left join t_competition_introduction competition on style_show.competition_id = competition.competition_id
where style_show.show_status = 1 and style_show.delete_status = 0 and style_show.competition_id = #{comId}
and style_show.year_id = #{theYear}
