mapper.update:
update t_article
set delete_status = 1, gmt_modify = CURRENT_TIMESTAMP
where  art_id = #{id}

mapper.select:
select count(*) from t_art_tag
where 
tag_id = #{tagId}

select

from t_article
where
 art_tag_name = #{param1}
 order by gmt_create desc
limit #{param2}, #{param3}

select
from t_article where
art_id in (select art_id from t_art_tag where tag_id = #{tagId}) order by gmt_create desc limit #{curPage}, #{size}

mapper.update:
update t_article
set art_viewcount = #{param2}, gmt_modify = CURRENT_TIMESTAMP
where 
artNo = #{param1}
and art_viewcount < #{param2}
